from flask_sqlalchemy import SQLAlchemy
from flask_bcrypt import Bcrypt

bcrypt = Bcrypt()
db = SQLAlchemy()

class User(db.Model):
    """User in the system."""

    __tablename__ = 'users'

    id = db.Column(
        db.Integer,
        primary_key=True,
    )

    email = db.Column(
        db.Text,
        nullable=False,
        unique=True,
    )

    username = db.Column(
        db.Text,
        nullable=False,
        unique=True,
    )

    name = db.Column(
        db.Text,
        nullable=False,
    )

    user_type = db.Column(
        db.Text,
        nullable=False,
    )

    image_url = db.Column(
        db.Text,
        default="data:image/jpeg;base64,"
    )

    bio = db.Column(
        db.Text,
    )

    password = db.Column(
        db.Text,
        nullable=False,
    )

    routes = db.relationship('Route', backref='users')

    @classmethod
    def signup(cls, username, name, email, password, image_url, bio, user_type):
        """Sign up user.

        Hashes password and adds user to system.
        """

        hashed_pwd = bcrypt.generate_password_hash(password).decode('UTF-8')

        user = User(
            username=username,
            name=name,
            email=email,
            password=hashed_pwd,
            image_url=image_url,
            bio=bio,
            user_type=user_type,
        )

        db.session.add(user)
        return user

    @classmethod
    def authenticate(cls, username, password):
        """Find user with `username` and `password`"""

        user = cls.query.filter_by(username=username).first()

        if user:
            is_auth = bcrypt.check_password_hash(user.password, password)
            if is_auth:
                return user

        return False

class Route(db.Model):
    """Routes in the system."""

    __tablename__ = 'routes'

    id = db.Column(
        db.Integer,
        primary_key=True,
    )

    name = db.Column(
        db.Text,
        nullable=False,
    )

    section = db.Column(
        db.Text,
        nullable=False,
    )

    color = db.Column(
        db.Text,
        nullable=False,
    )

    grade = db.Column(
        db.Text,
        nullable=False,
    )

    image_url = db.Column(
        db.Text,
        nullable=False,
    )

    description = db.Column(
        db.Text,
        nullable=False,
    )

    setter_id = db.Column(
        db.Integer,
        db.ForeignKey('users.id')
    )

    setter = db.relationship('User', backref='routes')

def connect_db(app):

    db.app = app
    db.init_app(app)